    version_number,
    version_name,
    config_snapshot,
    changes_summary,
    reasoning,
    created_by
  ) VALUES (
    p_bucket_id,
    v_version_number,
    p_version_name,
    p_config_snapshot,
    p_changes_summary,
    p_reasoning,
    'system'
  );

  -- Update bucket current version
  UPDATE bucket_instances
  SET current_version = v_version_number,
      updated_at = NOW()
  WHERE bucket_id = p_bucket_id;

  RETURN v_version_number;
END;
