    ) as total_requests
  FROM telemetry_features f
  LEFT JOIN telemetry_performance p ON p.install_id = f.install_id
  WHERE f.install_id = p_install_id
    AND f.last_used_at >= NOW() - (p_days || ' days')::INTERVAL
  GROUP BY f.feature_name
