CREATE FUNCTION public.track_url_fetch(p_url text, p_url_type character varying, p_status_code integer, p_success boolean) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_url_id INTEGER;
    v_url_hash VARCHAR(64);
