    pi.user_id,
    pi.total_slots,
    count(ii.slot_index) AS used_slots,
    (pi.total_slots - count(ii.slot_index)) AS empty_slots,
    round((((count(ii.slot_index))::numeric / (pi.total_slots)::numeric) * (100)::numeric), 2) AS capacity_percent,
